import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Recommendations = () => {
  const [recommendations, setRecommendations] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [selectedMember, setSelectedMember] = useState('');
  const [trends, setTrends] = useState([]);
  const [showTrends, setShowTrends] = useState(false);
  const [teamMembers, setTeamMembers] = useState([]);

  // Fetch team members from backend
  useEffect(() => {
    axios.get('/api/v1/team')
      .then(res => setTeamMembers(res.data))
      .catch(() => setTeamMembers([]));
  }, []);

  // Mock data for demonstration
  const mockRecommendations = [
    {
      skill_name: "Machine Learning Operations (MLOps)",
      description: "Learn to deploy, monitor, and maintain machine learning models in production environments.",
      priority: "High",
      learning_path: [
        "Docker and containerization basics",
        "Kubernetes fundamentals",
        "ML model deployment strategies",
        "Monitoring and observability tools",
        "CI/CD for ML pipelines"
      ],
      estimated_time: "8-12 weeks",
      market_demand: "High",
      trend_relevance: "MLOps is rapidly growing as companies scale ML deployments",
      source_evidence: [
        "GitHub trending repositories show 40% increase in MLOps tools",
        "Job market data indicates 60% growth in MLOps roles",
        "Tech blogs highlight MLOps as critical for AI success"
      ]
    },
    {
      skill_name: "Cloud-Native Development",
      description: "Develop applications designed to run in cloud environments with scalability and resilience.",
      priority: "Medium",
      learning_path: [
        "Cloud computing fundamentals (AWS/Azure/GCP)",
        "Microservices architecture",
        "Serverless computing",
        "Container orchestration",
        "Cloud security best practices"
      ],
      estimated_time: "6-10 weeks",
      market_demand: "High",
      trend_relevance: "Cloud-native development is becoming the standard for modern applications",
      source_evidence: [
        "Industry reports show 80% of new applications are cloud-native",
        "Cloud providers report 50% increase in serverless adoption",
        "Developer surveys indicate cloud skills as top priority"
      ]
    },
    {
      skill_name: "Data Engineering",
      description: "Build and maintain data pipelines, warehouses, and infrastructure for data-driven applications.",
      priority: "Medium",
      learning_path: [
        "SQL and database design",
        "ETL/ELT processes",
        "Big data technologies (Spark, Hadoop)",
        "Data warehousing concepts",
        "Data quality and governance"
      ],
      estimated_time: "10-16 weeks",
      market_demand: "High",
      trend_relevance: "Data engineering is critical for AI and analytics success",
      source_evidence: [
        "Data engineering roles show 45% year-over-year growth",
        "Companies report 70% increase in data pipeline investments",
        "Tech conferences highlight data engineering as key trend"
      ]
    }
  ];

  useEffect(() => {
    setRecommendations(mockRecommendations);
  }, []);

  const fetchTrends = async (role) => {
    try {
      setLoading(true);
      const response = await axios.get(`/api/v1/trends/${encodeURIComponent(role)}`);
      setTrends(response.data.trends || []);
      setShowTrends(true);
    } catch (err) {
      setError('Failed to fetch current trends');
    } finally {
      setLoading(false);
    }
  };

  const getPriorityColor = (priority) => {
    switch (priority.toLowerCase()) {
      case 'high': return 'bg-red-100 text-red-800 border-red-200';
      case 'medium': return 'bg-yellow-100 text-yellow-800 border-yellow-200';
      case 'low': return 'bg-green-100 text-green-800 border-green-200';
      default: return 'bg-gray-100 text-gray-800 border-gray-200';
    }
  };

  const getDemandColor = (demand) => {
    switch (demand.toLowerCase()) {
      case 'high': return 'text-green-600';
      case 'medium': return 'text-yellow-600';
      case 'low': return 'text-red-600';
      default: return 'text-gray-600';
    }
  };

  return (
    <div className="min-h-screen bg-gray-50 py-8">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="mb-8">
          <h1 className="text-3xl font-bold text-gray-900 mb-2">
            Dynamic Skill Recommendations
          </h1>
          <p className="text-gray-600">
            Personalized skill recommendations based on real-time industry trends and market analysis
          </p>
        </div>
        <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6 mb-8">
          <div className="flex flex-wrap gap-4 items-center">
            <div className="flex-1 min-w-64">
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Team Member
              </label>
              <select
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                onChange={(e) => setSelectedMember(e.target.value)}
                value={selectedMember}
              >
                <option value="">Select a team member</option>
                {teamMembers.map((member, idx) => (
                  <option key={idx} value={member.name}>
                    {member.name} ({member.role})
                  </option>
                ))}
              </select>
            </div>
            <div className="flex gap-3">
              <button
                onClick={() => fetchTrends('Software Engineer')}
                disabled={loading}
                className="px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700 disabled:opacity-50"
              >
                {loading ? 'Loading...' : 'View Trends'}
              </button>
              <button
                onClick={() => setShowTrends(!showTrends)}
                className="px-4 py-2 bg-gray-600 text-white rounded-md hover:bg-gray-700"
              >
                {showTrends ? 'Hide Trends' : 'Show Trends'}
              </button>
            </div>
          </div>
        </div>

        {/* Error Display */}
        {error && (
          <div className="bg-red-50 border border-red-200 rounded-md p-4 mb-6">
            <div className="flex">
              <div className="flex-shrink-0">
                <svg className="h-5 w-5 text-red-400" viewBox="0 0 20 20" fill="currentColor">
                  <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zM8.707 7.293a1 1 0 00-1.414 1.414L8.586 10l-1.293 1.293a1 1 0 101.414 1.414L10 11.414l1.293 1.293a1 1 0 001.414-1.414L11.414 10l1.293-1.293a1 1 0 00-1.414-1.414L10 8.586 8.707 7.293z" clipRule="evenodd" />
                </svg>
              </div>
              <div className="ml-3">
                <h3 className="text-sm font-medium text-red-800">Error</h3>
                <div className="mt-2 text-sm text-red-700">{error}</div>
              </div>
            </div>
          </div>
        )}

        {/* Trends Section */}
        {showTrends && (
          <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6 mb-8">
            <h2 className="text-xl font-semibold text-gray-900 mb-4">
              Current Industry Trends
            </h2>
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
              {trends.length > 0 ? (
                trends.map((trend, index) => (
                  <div key={index} className="bg-blue-50 border border-blue-200 rounded-lg p-4">
                    <h3 className="font-medium text-blue-900 mb-2">{trend.title}</h3>
                    <p className="text-sm text-blue-700">{trend.description}</p>
                    {trend.source && (
                      <p className="text-xs text-blue-600 mt-2">Source: {trend.source}</p>
                    )}
                  </div>
                ))
              ) : (
                <div className="col-span-full text-center py-8 text-gray-500">
                  <svg className="mx-auto h-12 w-12 text-gray-400" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 19v-6a2 2 0 00-2-2H5a2 2 0 00-2 2v6a2 2 0 002 2h2a2 2 0 002-2zm0 0V9a2 2 0 012-2h2a2 2 0 012 2v10m-6 0a2 2 0 002 2h2a2 2 0 002-2m0 0V5a2 2 0 012-2h2a2 2 0 012 2v14a2 2 0 01-2 2h-2a2 2 0 01-2-2z" />
                  </svg>
                  <p className="mt-2">No trends available. Click "View Trends" to fetch current data.</p>
                </div>
              )}
            </div>
          </div>
        )}

        {/* Recommendations Grid */}
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
          {recommendations.map((rec, index) => (
            <div key={index} className="bg-white rounded-lg shadow-sm border border-gray-200 overflow-hidden">
              {/* Header */}
              <div className="p-6 border-b border-gray-200">
                <div className="flex items-start justify-between mb-3">
                  <h3 className="text-lg font-semibold text-gray-900">{rec.skill_name}</h3>
                  <span className={`px-2 py-1 text-xs font-medium rounded-full border ${getPriorityColor(rec.priority)}`}>
                    {rec.priority} Priority
                  </span>
                </div>
                <p className="text-gray-600 mb-4">{rec.description}</p>
                
                <div className="flex items-center justify-between text-sm">
                  <span className="text-gray-500">
                    Estimated time: <span className="font-medium">{rec.estimated_time}</span>
                  </span>
                  <span className={`font-medium ${getDemandColor(rec.market_demand)}`}>
                    Market demand: {rec.market_demand}
                  </span>
                </div>
              </div>

              {/* Learning Path */}
              <div className="p-6 border-b border-gray-200">
                <h4 className="font-medium text-gray-900 mb-3">Learning Path</h4>
                <ol className="space-y-2">
                  {rec.learning_path.map((step, stepIndex) => (
                    <li key={stepIndex} className="flex items-start">
                      <span className="flex-shrink-0 w-6 h-6 bg-blue-100 text-blue-600 rounded-full flex items-center justify-center text-xs font-medium mr-3 mt-0.5">
                        {stepIndex + 1}
                      </span>
                      <span className="text-gray-700">{step}</span>
                    </li>
                  ))}
                </ol>
              </div>

              {/* Trend Relevance */}
              <div className="p-6 border-b border-gray-200">
                <h4 className="font-medium text-gray-900 mb-3">Why This Skill is Trending</h4>
                <p className="text-gray-600 text-sm mb-3">{rec.trend_relevance}</p>
                
                <h5 className="font-medium text-gray-900 mb-2">Source Evidence</h5>
                <ul className="space-y-1">
                  {rec.source_evidence.map((evidence, evidenceIndex) => (
                    <li key={evidenceIndex} className="text-sm text-gray-600 flex items-start">
                      <span className="text-blue-500 mr-2">•</span>
                      {evidence}
                    </li>
                  ))}
                </ul>
              </div>

              {/* Actions */}
              <div className="p-6 bg-gray-50">
                <div className="flex gap-3">
                  <button className="flex-1 px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700 transition-colors">
                    Start Learning
                  </button>
                  <button className="px-4 py-2 border border-gray-300 text-gray-700 rounded-md hover:bg-gray-50 transition-colors">
                    Save for Later
                  </button>
                </div>
              </div>
            </div>
          ))}
        </div>

        {/* Empty State */}
        {recommendations.length === 0 && !loading && (
          <div className="text-center py-12">
            <svg className="mx-auto h-12 w-12 text-gray-400" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9.663 17h4.673M12 3v1m6.364 1.636l-.707.707M21 12h-1M4 12H3m3.343-5.657l-.707-.707m2.828 9.9a5 5 0 117.072 0l-.548.547A3.374 3.374 0 0014 18.469V19a2 2 0 11-4 0v-.531c0-.895-.356-1.754-.988-2.386l-.548-.547z" />
            </svg>
            <h3 className="mt-2 text-sm font-medium text-gray-900">No recommendations</h3>
            <p className="mt-1 text-sm text-gray-500">
              Get started by selecting a team member and generating recommendations.
            </p>
          </div>
        )}
      </div>
    </div>
  );
};

export default Recommendations;
